CREATE TABLE PERSONNE (PERSONNE_ID INTEGER NOT NULL, AGE INTEGER, NOM VARCHAR(255), PRENOM VARCHAR(255), PRIMARY KEY (PERSONNE_ID))
CREATE TABLE CHIEN (CHIEN_ID INTEGER NOT NULL, NOM VARCHAR(255), CHENIL_ID INTEGER, PROPRIO_ID INTEGER, PRIMARY KEY (CHIEN_ID))
CREATE TABLE CHENIL (CHENIL_ID INTEGER NOT NULL, PRIMARY KEY (CHENIL_ID))
CREATE TABLE PATHOLOGIE (PATHOLOGIE_NAME VARCHAR(255) NOT NULL, DESCRIPTION VARCHAR(255), PRIMARY KEY (PATHOLOGIE_NAME))
CREATE TABLE CHIEN_PATHOLOGIE (CHIEN_ID INTEGER NOT NULL, PATHOLOGIE_NAME VARCHAR(255) NOT NULL, PRIMARY KEY (CHIEN_ID, PATHOLOGIE_NAME))
ALTER TABLE CHIEN ADD CONSTRAINT FK_CHIEN_PROPRIO_ID FOREIGN KEY (PROPRIO_ID) REFERENCES PERSONNE (PERSONNE_ID)
